mapper.update:
update item
set itemName=#{updateParam.itemName},
    price=#{updateParam.price},
    quantity=#{updateParam.quantity}
where id = #{id}

mapper.select:
select id, itemName, price, quantity
from item
where id = #{id}

select id, itemName, price, quantity from item
and itemName like concat('%',#{itemName},'%')
and price <= #{maxPrice}
